CREATE table  IF NOT EXISTS  TB_TRACK_STATISTICS(ID String,FIRST_TIME UInt64,FIRST_DEVICE_ID String,FIRST_DEVICE_TYPE UInt8,FIRST_INFO_ID String,LAST_TIME UInt64,LAST_DEVICE_ID String,LAST_DEVICE_TYPE UInt8,LAST_INFO_ID String,ALL_CNT UInt64,CNT UInt64,DATA_TYPE UInt8,PT UInt64) ENGINE = MergeTree() PARTITION BY (toInt32(PT / 1000000)) ORDER BY (ID,DATA_TYPE);
CREATE table  IF NOT EXISTS  TB_TRACK_ALL(ID String,INFO_ID String,SHOT_TIME UInt64,DEVICE_ID String,DATA_TYPE UInt8,DEVICE_TYPE UInt8,PT UInt64)ENGINE = MergeTree() PARTITION BY (toInt32(PT / 1000000)) ORDER BY (ID,DATA_TYPE);
